import React, { useState, useEffect } from "react";

function Weather() {
  // this API needs to start as an object
  const [weatherData, setWeatherData] = useState({});
  const url =
    "https://api.openweathermap.org/data/3.0/onecall?lat=37.7621407&lon=-122.4745359&appid=6d794b4b65ee6ae6ad174f26bb0daf29&exclude=minutely,daily,hourly";

  useEffect(() => {
    // getWeather();
  }, [weatherData]);

  async function getWeather() {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        return setWeatherData(data);
      });
  }

  return (
    <>
      <p className="white-text">{weatherData.current}</p>
      <button onClick={getWeather}>TEST</button>
    </>
  );
}

export default Weather;
