import React, { useEffect, useState } from "react";
import "./timer.css";

function Timer() {
  const [seconds, setSeconds] = useState(1500);
  const [timeLeft, setTimeLeft] = useState(timeDisplay());
  const [isActive, setIsActive] = useState(false);

  // display

  function timeDisplay() {
    let time = {};

    if (seconds > 0) {
      time = {
        minutes: Math.floor(seconds / 60),
        seconds: seconds % 60 > 10 ? seconds % 60 : `0${seconds % 60}`,
      };
    }
    return time;
  }

  // add a second every 1000 milliseconds
  useEffect(() => {
    let interval = null;
    if (isActive) {
      interval = setInterval(() => {
        setSeconds((seconds) => seconds - 1);
        setTimeLeft((seconds) => seconds - 1);
      }, 1000);
    } else if (!isActive && seconds !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval); // return clearInterval for clean up
  }, [isActive, seconds]);

  function toggle() {
    setIsActive((prev) => !prev);
  }

  function reset() {
    setSeconds(1500);
    setIsActive(false);
  }

  return (
    <div>
      <h1 className="white-text">Focus</h1>
      <hr></hr>
      <div>
        <h1 className="white-text">{`${timeLeft.minutes}:${timeLeft.seconds}`}</h1>
        <button
          className={`timer-button-status-${isActive ? "active" : "inactive"}`}
          onClick={toggle}
        >
          {isActive ? "Pause" : "Start"}
        </button>
        <button className="timer-button" onClick={reset}>
          Reset
        </button>
      </div>
    </div>
  );
}

export default Timer;
